svg.script:
// Get the text element
var textElement = document.getElementById("interactiveText");

// Hover effect: Smooth transition of color and scale
textElement.addEventListener("mouseover", function() {
    textElement.setAttribute("fill", "#FF6347");  // Lighter shade on hover
    textElement.style.transition = "all 0.3s ease";  // Smooth transition
    textElement.setAttribute("transform", "scale(1.1)"); // Slightly scale the text
});

textElement.addEventListener("mouseout", function() {
    textElement.setAttribute("fill", "url(#gradient1)");  // Revert to gradient
    textElement.setAttribute("transform", "scale(1)"); // Return to original size
});

// Click effect: Bounce animation
textElement.addEventListener("click", function() {
    textElement.style.transition = "transform 0.2s ease";
    textElement.setAttribute("transform", "scale(0.9)"); // Shrink text on click
    setTimeout(function() {
        textElement.setAttribute("transform", "scale(1.1)"); // Slightly expand for bounce effect
    }, 200);
    setTimeout(function() {
        textElement.setAttribute("transform", "scale(1)"); // Reset to original size
    }, 400);

    // Example of redirecting to a URL on click (optional)
    // window.location.href = "https://www.datingapp.com";
});
